mapper.update:
/** updateAnalysis : 분석 정보를 수정한다. */ UPDATE SEEK_ANALYSIS SET
ANALYSIS_END_DT = CASE WHEN ANALYSIS_END_DT IS NULL THEN NOW() ELSE ANALYSIS_END_DT END,
ANALYSIS_STATUS_CCD = #{analysisStatusCcd}, ANALYSIS_MODEL = #{analysisModel}, ANALYSIS_TIME = CASE WHEN #{analysisTime} IS NOT NULL THEN #{analysisTime} ELSE ANALYSIS_TIME END, TOTAL_DETECTED_COUNT = #{totalDetectedCount} WHERE ANALYSIS_ID = #{analysisId}

/** updateAnalysisDatabaseContent : 분석 데이터베이스 내용 정보를 수정한다. */
UPDATE SEEK_ANALYSIS_DATABASE
SET CONTENT = #{content}
WHERE ANALYSIS_ID = #{analysisId}

mapper.select:
/** selectProcessingAnalysisList : 실행중인 분석 정보 목록을 조회한다. */

WHERE a.ANALYSIS_STATUS_CCD = 'PROCESSING'
ORDER BY a.CREATE_DT

mapper.update:
/** updateAnalysisStatus : 분석 정보 상태를 수정한다. */ UPDATE SEEK_ANALYSIS SET
ANALYSIS_START_DT = CASE WHEN ANALYSIS_START_DT IS NULL THEN NOW() ELSE ANALYSIS_START_DT END,
ANALYSIS_END_DT = CASE WHEN ANALYSIS_END_DT IS NULL THEN NOW() ELSE ANALYSIS_END_DT END,
ANALYSIS_STATUS_CCD = #{analysisStatusCcd} WHERE ANALYSIS_ID = #{analysisId}

mapper.insert:
/** insertAnalysisResultItems : 분석 정보 상태를 수정한다. */
INSERT INTO SEEK_ANALYSIS_RESULT_ITEMS (
	ANALYSIS_ID,
	DETECTION_TYPE_CCD,
	DETECTED_COUNT
) VALUES

	(
		#{item.analysisId},
		#{item.detectionTypeCcd},
		#{item.detectedCount}
	)

/** insertAnalysisError : 분석 오류 정보를 등록한다. */
SELECT COALESCE(MAX(ANALYSIS_ERROR_SN), 0) + 1 AS ANALYSIS_ERROR_SN FROM SEEK_ANALYSIS_ERROR WHERE ANALYSIS_ID = #{analysisId}
INSERT INTO SEEK_ANALYSIS_ERROR ( ANALYSIS_ID, ANALYSIS_ERROR_SN, ANALYSIS_DATA, ERROR_MESSAGE, EXCLUSION_DT, CREATE_DT ) VALUES ( #{analysisId}, #{analysisErrorSn}, #{analysisData}, #{errorMessage}, NULL, NOW() )

mapper.update:
/** updateAnalysisErrorExclusion : 분석 오류를 배제 처리한다. */
UPDATE SEEK_ANALYSIS_ERROR
SET EXCLUSION_DT = NOW()
WHERE ANALYSIS_ID = #{analysisId}
	AND EXCLUSION_DT IS NULL

/** updateAnalysisTargetColumnDynamically : 분석 대상 컬럼을 동적으로 수정한다. */
UPDATE ${tableName}
SET ${columnName} = #{newValue}
WHERE ${columnName} = #{oldValue}
