mapper.sql:
SELECT si.SENSITIVE_INFORMATION_ID, si.TARGET_TEXT, si.ESCAPE_TEXT, si.HIT_COUNT, si.SEVERITY_CCD, cc1.COM_CD_NM AS SEVERITY_CCD_NM, si.CREATE_DT, si.MODIFY_DT FROM SEEK_SENSITIVE_INFORMATION si LEFT JOIN SEEK_COM_CD cc1 ON si.SEVERITY_CCD = cc1.COM_CD AND cc1.GROUP_CD = 'SEVERITY_CCD'
AND si.SENSITIVE_INFORMATION_ID = #{schSensitiveInformationId}
AND si.SENSITIVE_INFORMATION_ID IN
#{schId}

