mapper.insert:
/** insertSensitiveInformationList : 민감 정보 목록을 등록한다. */ INSERT INTO SEEK_SENSITIVE_INFORMATION AS target ( SENSITIVE_INFORMATION_ID, TARGET_TEXT, ESCAPE_TEXT, HIT_COUNT, SEVERITY_CCD, CREATE_DT, MODIFY_DT ) VALUES
( #{item.sensitiveInformationId}, #{item.targetText}, #{item.escapeText}, #{item.hitCount}, #{item.severityCcd}, NOW(), NOW() )
ON CONFLICT (SENSITIVE_INFORMATION_ID) DO UPDATE SET TARGET_TEXT = EXCLUDED.TARGET_TEXT, ESCAPE_TEXT = EXCLUDED.ESCAPE_TEXT, HIT_COUNT = target.HIT_COUNT + EXCLUDED.HIT_COUNT, SEVERITY_CCD = EXCLUDED.SEVERITY_CCD, MODIFY_DT = NOW()

		/** insertSensitiveInformationMappingList : 민감 정보 매핑 목록을 등록한다. */
		INSERT INTO SEEK_SENSITIVE_INFORMATION_MAPPING (
			ANALYSIS_RESULT_ID,
			SENSITIVE_INFORMATION_ID
		) VALUES

			(
				#{analysisResultId},
				#{item.sensitiveInformationId}
			)

        ON CONFLICT (ANALYSIS_RESULT_ID, SENSITIVE_INFORMATION_ID)
		DO NOTHING

/** insertSensitiveInformationTypeList : 민감 정보 유형 목록을 등록한다. */
INSERT INTO SEEK_SENSITIVE_INFORMATION_TYPES (
	SENSITIVE_INFORMATION_ID,
	DETECTION_TYPE_CCD
) VALUES

	(
		#{item.sensitiveInformationId},
		#{item.detectionTypeCcd}
	)

ON CONFLICT (SENSITIVE_INFORMATION_ID, DETECTION_TYPE_CCD)
DO NOTHING
