mapper.insert:
/** insertSensitiveInformationUnmaskHist : 민감 정보 언마스크 이력을 등록한다. */ INSERT INTO SEEK_SENSITIVE_INFORMATION_UNMASK_HIST ( REQUEST_ID, CLIENT_IP, USER_ID, SENSITIVE_INFORMATION_COUNT, CREATE_DT ) VALUES ( #{requestId}, #{clientIp},
#{userId}
(SELECT USER_ID FROM SEEK_AGENT WHERE CLIENT_IP = #{clientIp} LIMIT 1)
NULL
, #{sensitiveInformationCount}, NOW() )

/** insertSensitiveInformationUnmaskInfo : 민감 정보 언마스크 정보를 등록한다. */
INSERT INTO SEEK_SENSITIVE_INFORMATION_UNMASK_INFO (
	REQUEST_ID,
	SENSITIVE_INFORMATION_ID
) VALUES

	(
		#{requestId},
		#{item.sensitiveInformationId}
	)

ON CONFLICT (REQUEST_ID, SENSITIVE_INFORMATION_ID)
DO NOTHING

mapper.sql:
WITH SENSITIVE_INFORMATION_UNMASK_HIST AS ( SELECT USER_ID, SENSITIVE_INFORMATION_COUNT, CASE /* NORMAL 조건: 09:00 ~ 18:00 이면서 평일(월~금)인 경우 */ WHEN CREATE_DT::TIME >= '09:00:00' AND '18:00:00' >= CREATE_DT::TIME /* 평일(월요일=1, 금요일=5) 조건 추가 */ AND EXTRACT(DOW FROM CREATE_DT) BETWEEN 1 AND 5 THEN 'NORMAL' /* 그 외 (야간 또는 주말) */ ELSE 'ABNORMAL' END AS WORKING_HOUR_STATUS_CCD, CREATE_DT FROM SEEK_SENSITIVE_INFORMATION_UNMASK_HIST
AND CREATE_DT >= #{searchStartLocalDate}
AND #{searchEndLocalDateExclusive} > CREATE_DT
) SELECT USER_ID, COALESCE(SUM(COALESCE(SENSITIVE_INFORMATION_COUNT, 0)), 0) AS TOTAL_SENSITIVE_INFORMATION_COUNT, COALESCE(SUM(CASE WHEN WORKING_HOUR_STATUS_CCD = 'NORMAL' THEN COALESCE(SENSITIVE_INFORMATION_COUNT, 0) ELSE 0 END), 0) AS NORMAL_SENSITIVE_INFORMATION_COUNT, COALESCE(SUM(CASE WHEN WORKING_HOUR_STATUS_CCD = 'ABNORMAL' THEN COALESCE(SENSITIVE_INFORMATION_COUNT, 0) ELSE 0 END), 0) AS ABNORMAL_SENSITIVE_INFORMATION_COUNT, COUNT(1) AS TOTAL_REQUEST_COUNT, COALESCE(SUM(CASE WHEN WORKING_HOUR_STATUS_CCD = 'NORMAL' THEN 1 ELSE 0 END), 0) AS NORMAL_REQUEST_COUNT, COALESCE(SUM(CASE WHEN WORKING_HOUR_STATUS_CCD = 'ABNORMAL' THEN 1 ELSE 0 END), 0) AS ABNORMAL_REQUEST_COUNT, MAX(CREATE_DT) AS LAST_REQUEST_DT FROM SENSITIVE_INFORMATION_UNMASK_HIST GROUP BY USER_ID

