mapper.select:
/** selectFileDetailList : 파일 상세 목록을 조회한다. */ SELECT f.FILE_ID, fd.FILE_DETAIL_ID, f.FILE_SE_CCD, cc1.COM_CD_NM AS FILE_SE_CCD_NM, fd.FILE_NAME, fd.FILE_EXT, fd.FILE_SIZE, fd.CONTENT_TYPE, fd.SAVE_PATH, fd.SAVE_NAME, fd.SORT_SN, f.CREATE_DT FROM SEEK_FILE f JOIN SEEK_FILE_DETAIL fd ON f.FILE_ID = fd.FILE_ID LEFT JOIN SEEK_COM_CD cc1 ON f.FILE_SE_CCD = cc1.COM_CD AND cc1.GROUP_CD = 'FILE_SE_CCD' WHERE f.FILE_ID = #{fileId}
AND fd.FILE_DETAIL_ID = #{fileDetailId}
AND fd.SORT_SN = #{sortSn}

mapper.insert:
/** insertFile : 파일 정보를 등록한다. */
INSERT INTO SEEK_FILE (
	FILE_ID,
	FILE_SE_CCD,
	CREATE_DT
) VALUES (
	#{fileId},
	#{fileSeCcd},
	NOW()
)

/** insertFileDtl : 파일 상세 정보를 등록한다. */
SELECT COALESCE(MAX(SORT_SN), 0) + 1 AS SORT_SN FROM SEEK_FILE_DETAIL WHERE FILE_ID = #{fileId}
INSERT INTO SEEK_FILE_DETAIL ( FILE_DETAIL_ID, FILE_ID, FILE_NAME, FILE_EXT, FILE_SIZE, CONTENT_TYPE, SAVE_PATH, SAVE_NAME, SORT_SN, CREATE_DT ) VALUES ( #{fileDetailId}, #{fileId}, #{fileName}, #{fileExt}, #{fileSize}, #{contentType}, #{savePath}, #{saveName}, #{sortSn}, NOW() )

/** insertFileList : 파일 정보 목록을 등록한다.(중복 제거) */
INSERT INTO SEEK_FILE ( FILE_ID, FILE_SE_CCD, CREATE_DT ) SELECT DISTINCT x.FILE_ID, x.FILE_SE_CCD, NOW() FROM (
SELECT #{file.fileId} AS FILE_ID, #{file.fileSeCcd} AS FILE_SE_CCD
) x
SELECT 0
